mapper.select:
SELECT

FROM T_SYS_ROLE a
where 1 = 1
AND a.id > 0
AND a.platform_code = #{platformCode}

    and a.id != #{id}


    and a.role_name LIKE concat(concat('%',#{roleName}),'%')


    AND role_type = #{roleType}


    START WITH a.id = #{id}
    CONNECT BY PRIOR a.id = a.parent

ORDER BY
a.grade, a.orders asc

SELECT
a.id AS "id",
parent AS "pid",
role_name  AS "name",
(
SELECT NVL2(sir.role_id,'true','false') FROM T_SYS_STAFF_INST_ROLE sir INNER JOIN t_crs_sys_staff s ON sir.staff_id = s.id
WHERE sir.ROLE_ID = a.ID
AND sir.institution_id = #{instId}
AND sir.staff_id = #{staffId}
) AS checked
FROM T_SYS_ROLE a

    role_type = #{roleType}
    AND a.platform_code = #{platformCode}

ORDER BY orders ASC

SELECT
a.role_name AS "roleName"
FROM T_SYS_ROLE a
INNER JOIN T_SYS_STAFF_INST_ROLE sir ON sir.role_id = a.id

    sir.staff_id = #{staffId}
    AND sir.institution_id = #{instId}

SELECT case when grade=0 then 'open' else 'closed' end as state,

FROM T_SYS_ROLE a
where parent = #{id}
AND a.platform_code = #{platformCode}
ORDER BY a.grade, a.orders asc
